document.addEventListener("DOMContentLoaded",()=>{let e=Array.from(document.querySelectorAll(".game-container"));const t=Date.now();let o=[];e.forEach(e=>{const n=1e3*e.dataset.scroll;n&&n>t&&o.push(e)});const n=document.getElementById("next-game");o.length>0?n.addEventListener("click",()=>{if(n.hasAttribute("data-date")){const e=o[0].parentNode,t=e.previousElementSibling;t.scrollIntoView({behavior:"smooth"})}else o[0].scrollIntoView({behavior:"smooth"})}):n.style.display="none"});