document.addEventListener("DOMContentLoaded",()=>{let e=Array.from(document.querySelectorAll(".game-container"));const t=Date.now();let n=[];e.forEach(e=>{const o=1e3*e.dataset.scroll;o&&o>t&&n.push(e)});const o=document.getElementById("next-game");n.length>0?o.addEventListener("click",()=>{const e=n[0].parentNode,t=e.previousElementSibling;t.scrollIntoView({behavior:"smooth"})}):o.style.display="none"});